CREATE TABLE IF NOT EXISTS `character_homunculus` (
	`char_id` INT NOT NULL,
	`slot` TINYINT UNSIGNED NOT NULL DEFAULT '0',
	`homunculus_id` TINYINT UNSIGNED NOT NULL,
	`level` TINYINT UNSIGNED NOT NULL DEFAULT '1',
	`exp` INT UNSIGNED NOT NULL DEFAULT '0',
	`skill1_level` TINYINT UNSIGNED NOT NULL DEFAULT '0',
	`skill2_level` TINYINT UNSIGNED NOT NULL DEFAULT '0',
	`skill3_level` TINYINT UNSIGNED NOT NULL DEFAULT '0',
	`skill4_level` TINYINT UNSIGNED NOT NULL DEFAULT '0',
	`skill5_level` TINYINT UNSIGNED NOT NULL DEFAULT '0',
	`active` TINYINT UNSIGNED NOT NULL DEFAULT '0'
) ENGINE = InnoDB DEFAULT CHARSET=utf8;